#import datetime Moduleimport datetime#function import current timedef gettime():    time=datetime.datetime.now()    return time#Add Log Function for Adding Informationdef add_log():    name=input("Please Enter Your Name ")    print("Choose Option  for Log :\n1.Exercise Log \n2. Diet Log")    ex_log_input=int(input())    if ex_log_input==1:        exercise_name=input("Enter Exercise You Doing : ")        exercise_file=name+'_exercise_log.txt'        with open(exercise_file,'a') as ef:            ef.write(f'* {exercise_name} : {gettime()} -Time\n')        ef.close()        print('Exercise Log Successfully Updated.')    if ex_log_input==2:        diet_name=input("Enter The Food  you are Eating : ")        diet_file=name+'_diet_log.txt'        with open(diet_file,'a') as df:            df.write(f'* {diet_name} : {gettime()} -Time\n')        df.close()        print('Diet Log Successfully Updated.')#Retrive Log Function for Retrieving Informationdef retrieve_log():    name1=input("Enter Your Name : ")    print("\nChoose Option  for Retieval Information :\n1.Exercise Log \n2. Diet Log")    re_log_input = int(input())    if re_log_input == 1:        exercise_file = name1+'_exercise_log.txt'        try :            with open(exercise_file,'r') as ef:                print("\nList of Exercise Log : \n")                for i in ef:                    print(i,end='')                print()        except:            print(f"{name1} , No Records of You.\nExecution End !!!\n")    elif re_log_input == 2:        diet_file = name1+'_diet_log.txt'        try :            with open(diet_file,'r') as df:                print('\nList of Diet Log : \n')                for i in df:                    print(i,end='')                print()        except:            print(f'{name1}, No Records of You.\nExecution End !!!\n')#Main Function ()def Main():    while True :        print("\nChoose Your Option : \n1. Add Log \n2. Retrieve Log\n3. End")        n=int(input())        if n==1:            add_log()        elif n==2:            retrieve_log()        elif n==3:            print("Thank You !!!")            break        else:            print("You Entered Wrong Key !!! Try Again")            Main()#Driver Functionif __name__=='__main__':    print("*** Welcome To Health Management System ***")    Main()